function getStorageArr(filter, callback){
    var wallets = [];
    storage.get(null, function(items) {
        for (var key in items) {
            if (items.hasOwnProperty(key)) {
                var tobj = JSON.parse(items[key]);
                 if(tobj.type==filter){
                    tobj['addr']=key;
                    wallets.push(tobj);
                 }
            }
        }
        wallets.sort(sortByNickName);
        callback(wallets);
    });
}
